ruleSearch.bookList:
.bookbox
<js>
result.toArray().slice(0, 3).map(el => {
	var url = String(el.select('a:eq(0)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
	var J = org.jsoup.Jsoup.parse(java.ajax(url));
	var o = selector => String(J.select(selector).text());
	return {
		name: o('.book-name').replace(/(全文|小说|免费阅读|最新章节).*|[(（].*[）)]/g, ''),
		author: o('.au-name'),
		kind: o('.label') + ',' + o('.state').replace(/中|已/,''),
    wordCount: o('.nums span:eq(0) i'),
		latest: o('.book-new-chapter .tit').replace(/^(正文|VIP章节|最新章节)?(\s+|_)|[\(（【].*[求更谢乐发推].*/g, '') + ' • ' + o('.time').replace(/· |\s\d+:.*/g,''),
		intro: o('.book-dec').replace(/.*(观看小说|简介)[:：]|各位书友.*/g, '').replace(/\s+/g, ''),
		cover: J.select('.book-img-w img').attr('src'),
		url: url,
	};
}); 
</js>

